INSERT INTO `tbl_content` VALUES (3, 6, 1, 1, 'Dự án trọng điểm thu hút đầu tư giai đoạn 2020 - 2025: Dự án trung Tâm Logistics cảng Hàng không quốc tế Đà Nẵng', 'du-an-trong-diem-thu-hut-dau-tu-giai-doan-2020-2025-du-an-trung-tam-logistics-cang-hang-khong-quoc-te-da-nang', 'Cảng Hàng không quốc tế Đà Nẵng\r\nTrung tâm Logistics chuyên dụng hàng không, quy mô đến năm 2030 là 4 ha', '', 'http://localhost/newhome/medias/contents/2a11c3bedc502292f4f01be90424d6aa.jpg', '<p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; color: rgb(51, 51, 51); font-family: Arial; text-align: justify;\"><strong style=\"font-weight: bold;\">Tên dự án: Trung tâm Logictics cảng Hàng không quốc tế Đà Nẵng</strong></p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; color: rgb(51, 51, 51); font-family: Arial; text-align: justify;\"><strong style=\"font-weight: bold;\">Địa điểm: </strong>Cảng Hàng không quốc tế Đà Nẵng</p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; color: rgb(51, 51, 51); font-family: Arial; text-align: justify;\"><strong style=\"font-weight: bold;\">Thông số kỹ thuật: </strong>Trung tâm Logistics chuyên dụng hàng không, quy mô đến năm 2030 là 4 ha</p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; color: rgb(51, 51, 51); font-family: Arial; text-align: justify;\"><strong style=\"font-weight: bold;\">Tổng vốn đầu tư (dự kiến): </strong>Nhà đầu tư đề xuất</p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; color: rgb(51, 51, 51); font-family: Arial; text-align: justify;\"><strong style=\"font-weight: bold;\">Phân kỳ đầu tư</strong>: 2021 - 2025</p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; color: rgb(51, 51, 51); font-family: Arial; text-align: justify;\"><strong style=\"font-weight: bold;\">Hình thức đầu tư: </strong>100% vốn nhà đầu tư</p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; color: rgb(51, 51, 51); font-family: Arial