INSERT INTO `tbl_seo` VALUES (42, 'Kiến trúc sư Nguyễn Nga: Bảo tồn cầu Long Biên như một di sản', 'http://localhost/newhome/kien-truc-su-nguyen-nga-bao-ton-cau-long-bien-nhu-mot-di-san-78.html', 'http://localhost/newhome/medias/contents/511_nguyen-nga-12_51325832.jpg', 'Kiến trúc sư Nguyễn Nga: Bảo tồn cầu Long Biên như một di sản', 'Kiến trúc sư Nguyễn Nga: Bảo tồn cầu Long Biên như một di sản', 'Dự án Bảo tồn, cải tạo cầu Long Biên có ý nghĩa về văn hóa, giáo dục đã rõ. Nhưng theo chị, dự án sau khi hoàn thành có đem lại nhiều lợi ích gì về kinh tế hay không? ', 0, 0, 1);
INSERT INTO `tbl_seo` VALUES (43, 'Những điều thú vị về cầu Long Biên – “Chứng nhân lịch sử” của Hà Nội', 'http://localhost/newhome/nhung-dieu-thu-vi-ve-cau-long-bien-–-chung-nhan-lich-su-cua-ha-noi-79.html', 'http://localhost/newhome/medias/contents/Cau long bien.jpg', 'Những điều thú vị về cầu Long Biên – “Chứng nhân lịch sử” của Hà Nội', 'Những điều thú vị về cầu Long Biên – “Chứng nhân lịch sử” của Hà Nội', 'Cầu Long Biên là cây cầu được gọi với danh xưng “chứng nhân lịch sử” của nước ta. Trải qua bao thăng trầm của thời gian cùng biến cố lịch sử, cây cầu vẫn hiên ngang và trở thành một trong những biểu tượng của Hà Nội. Giờ hãy cùng tìm hiểu về cây cầu nổi t', 0, 0, 1);
INSERT INTO `tbl_seo` VALUES (44, 'Cầu Long Biên - “chứng nhân” lịch sử', 'http://localhost/newhome/cau-long-bien-chung-nhan-lich-su-80.html', 'http://localhost/newhome/medias/contents/cau-long-bien-flycam_i78.PNG', 'Cầu Long Biên - “chứng nhân” lịch sử', 'Cầu Long Biên - “chứng nhân” lịch sử', '4h chiều ngày 9-10-1954, những tên lính Pháp cuối cùng đã rút khỏi Hà Nội qua cầu Long Biên xuống vùng đệm Hải Phòng. Sáng ngày 10-10-1954, bên phải cầu, bộ đội ta hồ hởi trở về tiếp quản Thủ đô. Cái ngày cây cầu trở thành \"chứng nhân\" lịch sử cho sự kiện', 0, 0, 1);
INSERT INTO `tbl_seo` VALUES (45, 'Cha đẻ - cầu Long Biên', 'http://localhost/newhome/cha-de-cau-long-bien-81.html', 'http://localhost/newhome/medias/contents/images1501823_7_1.jpg', 'Cha đẻ - cầu Long Biên', 'Cha đẻ - cầu Long Biên', '- Không ít người nhầm lẫn khi cho rằng “cha đẻ” của cầu Long Biên là kỹ sư lừng danh người Pháp Gustave Eiffel. Thực ra, người để lại công trình có tuổi hơn thế kỷ này là Paul Doumer (1857-1932) - Toàn quyền Đông Dương thời Việt Nam thuộc Pháp.', 0, 0, 1);
